function calculateUserPreferences(userData) {
  const allBeers = [...userData.saved_beers, ...userData.purchased_beers];
  const total = { 酸味: 0, 甘み: 0, 苦み: 0, コク: 0, 香り: 0 };

  allBeers.forEach(beer => {
    total.酸味 += beer.酸味;
    total.甘み += beer.甘み;
    total.苦み += beer.苦み;
    total.コク += beer.コク;
    total.香り += beer.香り;
  });

  const count = allBeers.length;
  return {
    酸味: (total.酸味 / count).toFixed(1),
    甘み: (total.甘み / count).toFixed(1),
    苦み: (total.苦み / count).toFixed(1),
    コク: (total.コク / count).toFixed(1),
    香り: (total.香り / count).toFixed(1)
  };
}

