// 五角形グラフの描画
function drawRadarChart(canvasId, data) {
  const ctx = document.getElementById(canvasId).getContext("2d");
  const labels = ["酸味", "甘み", "苦み", "コク", "香り"];
  const values = [data.酸味, data.甘み, data.苦み, data.コク, data.香り];

  const max = 5; // 最大値
  const step = (2 * Math.PI) / labels.length;

  ctx.clearRect(0, 0, 400, 400);
  ctx.translate(200, 200);

  // グラフ外枠の描画
  ctx.strokeStyle = "#3498db";
  ctx.beginPath();
  for (let i = 0; i < labels.length; i++) {
    const x = Math.cos(i * step - Math.PI / 2) * (values[i] / max) * 150;
    const y = Math.sin(i * step - Math.PI / 2) * (values[i] / max) * 150;
    if (i === 0) ctx.moveTo(x, y);
    else ctx.lineTo(x, y);
  }
  ctx.closePath();
  ctx.stroke();

  // ラベル描画
  ctx.fillStyle = "#2c3e50";
  labels.forEach((label, i) => {
    const x = Math.cos(i * step - Math.PI / 2) * 170;
    const y = Math.sin(i * step - Math.PI / 2) * 170;
    ctx.fillText(label, x - 15, y + 5);
  });

  ctx.resetTransform();
}

