function calculateSimilarity(userPrefs, beer) {
  return Math.sqrt(
    Math.pow(beer.酸味 - userPrefs.酸味, 2) +
    Math.pow(beer.甘み - userPrefs.甘み, 2) +
    Math.pow(beer.苦み - userPrefs.苦み, 2) +
    Math.pow(beer.コク - userPrefs.コク, 2) +
    Math.pow(beer.香り - userPrefs.香り, 2)
  );
}

function findBestBeerMatch(userPrefs, beerDatabase) {
  let bestMatch = null;
  let minDistance = Infinity;

  beerDatabase.forEach(beer => {
    const distance = calculateSimilarity(userPrefs, beer);
    if (distance < minDistance) {
      minDistance = distance;
      bestMatch = beer;
    }
  });

  return bestMatch;
}

import java.io.*;
import java.net.*;
import org.json.*;

public class BeerTasteAutoGenerator {

    public static class TasteProfile {
        public String name;
        public double bitterness;
        public double sweetness;
        public double sharpness;
        public double richness;
        public double aroma;
        public double sourness;

        public String toJson() {
            return String.format("{\"name\":\"%s\",\"bitterness\":%.1f,\"sweetness\":%.1f,\"sharpness\":%.1f,\"richness\":%.1f,\"aroma\":%.1f,\"sourness\":%.1f}",
                    name, bitterness, sweetness, sharpness, richness, aroma, sourness);
        }
    }

    public static JSONObject fetchBeerInfo(String beerName) throws Exception {
        String query = URLEncoder.encode(beerName, "UTF-8");
        String urlString = "https://api.punkapi.com/v2/beers?beer_name=" + query;
        URL url = new URL(urlString);
        HttpURLConnection conn = (HttpURLConnection) url.openConnection();
        conn.setRequestMethod("GET");

        BufferedReader reader = new BufferedReader(
                new InputStreamReader(conn.getInputStream()));
        StringBuilder response = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            response.append(line);
        }
        reader.close();

        JSONArray beers = new JSONArray(response.toString());
        return beers.length() > 0 ? beers.getJSONObject(0) : null;
    }

    public static TasteProfile generateFromBeer(JSONObject beer) {
        TasteProfile tp = new TasteProfile();
        tp.name = beer.getString("name");

        double abv = beer.optDouble("abv", 5.0);
        double ibu = beer.optDouble("ibu", 30.0);
        double og = beer.optJSONObject("target_og") != null ? beer.getJSONObject("target_og").optDouble("value", 1.050) : 1.050;
        double fg = beer.optJSONObject("target_fg") != null ? beer.getJSONObject("target_fg").optDouble("value", 1.010) : 1.010;

        tp.bitterness = Math.min(ibu / 60.0 * 5.0, 5.0);
        tp.sweetness = Math.max(0.0, Math.min((fg - 1.000) * 100 + (5.0 - abv), 5.0));
        tp.sharpness = Math.min(Math.max((abv - (fg - 1.000) * 100) / 2.0, 0.0), 5.0);
        tp.richness = Math.min((og - 1.040) * 125, 5.0);
        tp.aroma = Math.min((abv + Math.max(0, 30 - Math.abs(30 - ibu))) / 10.0, 5.0);
        tp.sourness = Math.max(0.0, Math.min((1.010 - fg) * 200 + (ibu < 15 ? 1.0 : 0.0), 5.0));

        return tp;
    }

    public static void main(String[] args) {
        try {
            String beerName = "Punk IPA"; // ←ここを自由に入力できるように
            JSONObject beer = fetchBeerInfo(beerName);
            if (beer != null) {
                TasteProfile profile = generateFromBeer(beer);
                System.out.println(profile.toJson());
            } else {
                System.out.println("ビール情報が見つかりませんでした。");
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
